import React, {useEffect, useState} from 'react';
import { 
    createStyles, 
    withStyles,
    Box,
    Container,
    Typography,
    Grid,
} from '@material-ui/core';
import WeatherTableRow from '../components/WeatherTableRow';
import WeatherTableHeader from '../components/WeatherTableHeader';

const WeatherTable = (props) => {
    const classes = props.classes;
    const ids = [
        2172797,
        1705545,
        1796228,
        1006984,
        1796247
    ]
    const [weatherData, setWeatherData] = useState(null);
    const fetchData = () => {
        setWeatherData(ids.map((id) => 
            fetch(`https://api.openweathermap.org/data/2.5/weather?id=${id}&appid=${process.env.REACT_APP_WEATHER_API_KEY}` )
            .then((response) => response.json())
            .then((data) => {
                return(
                    <WeatherTableRow
                    cityName = {data.name}
                    temperature = {data.main.temp}
                    minTemperature = {data.main.temp_min}
                    updatedAt = {Date()}
                    />
                )
            })
            )
        )
    }
    useEffect(() => {
        fetchData();
    }, []);
    return (
        <Box className={classes.weatherTableContainer}>
            <Container>
                <WeatherTableHeader
                   cityName = "City Name"
                   temperature = "Temperature"
                   minTemperature = "Min Temperature"
                   updatedAt =  "Updated At"
                />
                {weatherData}
            </Container>
        </Box>
    );
}

const useStyles = (theme) => createStyles({
    weatherTableContainer: {
        width: '100%',
        height: '100%',
        margin: '0px',
        padding: '0px',
        overflowX: 'hidden',
    },
});

export default withStyles(useStyles)(WeatherTable);
